let copyInput=document.querySelectorAll(".copy-input"),copyBtn=document.querySelectorAll(".copy-btn");function copyValue(){copyInput.forEach((t=>{if(this.dataset.copy==t.dataset.copy){t.select(),document.execCommand("copy"),t.selectionStart=t.value.length,this.querySelector(".tooltiptext").innerHTML="Скопировано"}}))}function outFunc(){this.querySelector(".tooltiptext").innerHTML="Копировать"}if(copyBtn.forEach((t=>{t.addEventListener("click",copyValue),t.addEventListener("touch",copyValue),t.addEventListener("mouseout",outFunc)})),document.querySelector("#time")){function startTimer(t,e){let o,n,c=t;setInterval((function(){o=parseInt(c/60,10),n=parseInt(c%60,10),o=o<10?"0"+o:o,n=n<10?"0"+n:n,e.textContent=o+":"+n,--c<0&&(c=t)}),1e3)}window.onload=function(){startTimer(1200,document.querySelector("#time"))}}